mapper.update:
UPDATE qr
   SET title = #{title}
      ,writer = #{writer}
      ,content = #{content}
WHERE qr_no = #{qrNo}

mapper.select:
SELECT *
FROM qr
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
ORDER BY reg_date DESC

mapper.delete:
DELETE FROM qr
WHERE parent_table = #{parentTable}
  AND parent_no = #{parentNo}
